import {useState, useEffect} from 'react';

import SendMessage from './SendMessage';
import DisplayMessage from './DisplayMessage';
import logo from './assets/logo.png';

function Header() {
    return <img src={logo} alt="Logo" />;
}

const Home = () => {
    const [info, setInfo] = useState(null);

    useEffect(() => {
        fetch('http://localhost:8000/info')
        .then(res => {
            return res.json();
        })
        .then(data => {
            setInfo(data);
        })
    }, [])

    return (  
        <div className="Home">
            <div id="top-banner"> 
                <div className="title">
                    <span>
                        Home
                    </span>
                </div>
            </div>
            <div id="links-banner" className="link">
                    <a href="/">Link 1</a>
                    <a href="/">Link 2</a>
                    <a href="/">Link 3</a>
                </div>
            
            <SendMessage />
            {info && <DisplayMessage info={info}/>}
        </div>
    );
}
 
export default Home;
