import { render } from "@testing-library/react";
import React from "react";
import ReactDOM from "react-dom";

const images = {
    white: "/assets/gridWnb.png",
    gold: "/assets/gridGnb.png"
}
// Written link navigation
const Links = props => {
    return (
        <div className="link">
            <a href="/">Home</a>
            <a href="/About">About</a>
            <a href="/FAQ">FAQ</a>
            <a href="/Rules">Rules</a>
        </div>
    )
}

// Handles grid image change on hover
const HoverGrid = props => {
    function over(e) {
        e.currentTarget.src = props.hover;
    }
    function out(e) {
        e.currentTarget.src = props.image;
    }
    return <img src={props.image} onMouseOver={over} onMouseOut={out} alt="M" height="50px"/>
};

// Grid-style navigation
class Grid extends React.Component {
    container = React.createRef();
    state = {
        open: false,
    };

    handleButtonClick = () => {
        this.setState(state=> {
            return {
                open: !state.open,
            };
        });
    };

    componentDidMount() {
        document.addEventListener("mousedown", this.handleClickOutside);
    }

    componentWillUnmount() {
        document.removeEventListener("mousedown", this.handleClickOutside);
    }

    handleClickOutside = event => {
        if (this.container.current && !this.container.current.contains(event.target)) {
            this.setState({
                open: false,
            });
        }
    };

    getImageName = () => this.state.open ? 'white' : 'gold'

    render() {
        const imageName = this.getImageName();

        return (
            <div className="gridNav">   
                <div className="gridCont" ref={this.container}>
                    <button 
                        type="button" 
                        className="button" 
                        onClick={this.handleButtonClick}
                    >
                        a
                        <HoverGrid image={images.imageName} hover={images.white}/>
                    </button>
                    {this.state.open && (
                        <div className="dropdown">
                            <ul>
                                <li>Option 1</li>
                                <li>Option 2</li>
                                <li>Option 3</li>
                                <li>Option 4</li>
                            </ul>
                        </div>
                    )}
                </div>
            </div>
        )
    }
}

// Determines whether to use grid-style or written link based on resolution
function DynamicLink () {
    const updateWidthAndHeight = () => {
        setWidth(window.innerWidth);
        setHeight(window.innerHeight);
    };

    React.useEffect(() => {
        window.addEventListener("resize", updateWidthAndHeight);
        return () => window.removeEventListener("resize", updateWidthAndHeight);
    });

    const [width, setWidth] = React.useState(window.innerWidth);
    const [height, setHeight] = React.useState(window.innerHeight);

    if(width > 600) {
        return(<Links />)
    }
    else { 
        return (<Grid />)
    }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<DynamicLink />, rootElement); 
export default DynamicLink;
