import React from "react";
import ReactDOM from "react-dom";

const white = "/assets/logoWhite.png";
const gold = "/assets/logoGold.png";

const HoverImage = props => {
    function over(e) {
        e.currentTarget.src = props.hoverImage;
    }
    function out(e) {
        e.currentTarget.src = props.normalImage;
    }
    return <img src={props.normalImage} onMouseOver={over} onMouseOut={out} alt="M" className="headerImage" height={props.height} />
};

function HeaderHover() {
    const updateWidthAndHeight = () => {
        setWidth(window.innerWidth);
        setHeight(window.innerHeight);
    };

    React.useEffect(() => {
        window.addEventListener("resize", updateWidthAndHeight);
        return () => window.removeEventListener("resize", updateWidthAndHeight);
    });

    const [width, setWidth] = React.useState(window.innerWidth);
    const [height, setHeight] = React.useState(window.innerHeight);

    let marg = 0;
    if(width < 600) {
        let marg = 5;
    }
    else {
        let marg = 10;
    }

    return (
            <HoverImage margin={marg} height={60} normalImage={gold} hoverImage={white} />
    )
}

const rootElement = document.getElementById("root");
ReactDOM.render(<HeaderHover />, rootElement); 
export default HeaderHover;
