import {useState, useEffect} from 'react';

import SendMessage from './SendMessage';
import DisplayMessage from './DisplayMessage';
import HeaderHover from './HomeScripts/HeaderHover'

const Home = () => {
    const [info, setInfo] = useState(null);

    useEffect(() => {
        fetch('http://localhost:8000/info')
        .then(res => {
            return res.json();
        })
        .then(data => {
            setInfo(data);
        })
    }, [])

    return (  
        <div className="Home">
            <div id="page-container">
                <div id="content-wrap">
                    <div id="headerImageContainer">
                        <a href="http://mtu.edu/">
                            <HeaderHover />
                        </a>
                    </div>
                    <div id="top-banner"> 
                        <div className="title">
                            <span>
                                Home
                            </span>
                        </div>
                    </div>
                    <div id="links-banner" className="link">
                            <a href="/">About</a>
                            <a href="/">FAQ</a>
                            <a href="/">Rules</a>
                    </div>
                    
                    <SendMessage />
                    {info && <DisplayMessage info={info}/>}
                </div>
                <footer id="footer">
                    content
                 </footer>
            </div>
        </div>
    );
}
 
export default Home;
