import React from "react";
import ReactDOM from "react-dom";
import ImageHover from "./ImageHover";

const images = {
    white: "/assets/gridWnb.png",
    gold: "/assets/gridGnb.png",
}
// Written link navigation
const Links = props => {
    return (
        <div className="link">
            <a href="/">Home</a>
            <a href="/About">About</a>
            <a href="/FAQ">FAQ</a>
            <a href="/Rules">Rules</a>
        </div>
    )
}

// Handles grid image change on hover
const Hover = props => {
    function over(me) {
        me.currentTarget.src = props.hover;
    }
    function out(me) {
        me.currentTarget.src = props.default;
    }
    return <img src={props.default} onMouseOver={over} onMouseOut={out} alt="M" height="50px"/>
};

// Grid-style navigation
class Grid extends React.Component {
    container = React.createRef();
    state = {
        open: false,
    };

    handleButtonClick = () => {
        this.setState(state=> {
            return {
                open: !state.open,
            };
        });
    };

    componentDidMount() {
        document.addEventListener("mousedown", this.handleClickOutside);
    }

    componentWillUnmount() {
        document.removeEventListener("mousedown", this.handleClickOutside);
    }

    handleClickOutside = event => {
        if (this.container.current && !this.container.current.contains(event.target)) {
            this.setState({
                open: false,
            });
        }
    };

    render() {
        let grid_class = this.state.open ? "gridNavO" : "gridNavC";     // this.state.open -> grid background is gold; !this.state.open -> grid background is transparent

        return (
            <div> {/* Have to return everything in a container */}
                <div className={grid_class} ref={this.container}>
                    <button type="button" className={"gridBtn"} onClick={this.handleButtonClick}>

                        {/* this.state.open -> grid is white; !this.state.open -> grid is gold, white on hover */}
                        {this.state.open && (<Hover height={50} default={images.white} hover={images.white} className={"none"} altText={"Navigation"} />)}
                        {!this.state.open && (<Hover height={50} default={images.gold} hover={images.white} className={"none"} altText={"Navigation"} />)}

                    </button>
                    {/* Displays dropdown only when list is open */}
                    {this.state.open && (
                        <div className="dropdown">
                            <ul>
                            <li> <a href="/">Home</a> </li>
                            <li> <a href="/About">About</a> </li>
                            <li> <a href="/FAQ">FAQ</a> </li>
                            <li> <a href="/Rules">Rules</a> </li>
                            </ul>
                        </div>
                    )}
                </div>
            </div>
        )
    }
}

// Determines whether to use grid-style or written link based on resolution
// Has functionality for "height" of window but it's unused and commented out
function DynamicLink () {
    const updateWidthAndHeight = () => {
        setWidth(window.innerWidth);
        // setHeight(window.innerHeight);
    };

    // Checks if user has resized window and updates width/height as necessary
    React.useEffect(() => {
        window.addEventListener("resize", updateWidthAndHeight);
        return () => window.removeEventListener("resize", updateWidthAndHeight);
    });

    const [width, setWidth] = React.useState(window.innerWidth);
    // const [height, setHeight] = React.useState(window.innerHeight); 

    // Chooses whether to show written links or grid links based on window width
    if(width > 600) {
        return (<Links />)
    }
    else { 
        return (<Grid />)
    }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<DynamicLink />, rootElement); 
export default DynamicLink;
