// import {useState, useEffect} from 'react' // Not currently used?
import React from "react"
import {Navbar, Footer} from '../subscript/universal'

const LoginHelper = props => {
    if (props.size > 600) {

    }
    return (  
        <div className="Login">
             <div id="page-container">
                <div id="content-wrap">
                    <Navbar pageID={10}/>
                    <div id="login-container">
                        <h1>Log in</h1>
                        <h2>Email</h2>

                        {/* */}
                        <form > 
                            <input
                            type = "email"
                            placeholder = "Email"
                            required
                            //value {email}
                            //onChange={(e) => setEmail(e.target.value)}
                            />
                            <h2>Password</h2>
                            <input
                            type = "password"
                            required
                            //value {password}
                            //onChange={(e) => setPassword(e.target.value)}
                            />
                            {/* <h2></h2> */ }
                            <button>Login</button>
                        </form>
                        <a href="/SignUp">Create An Account</a>
                    </div>

                </div>
                <Footer />
            </div>
        </div>
    );
}

// Determines whether to use grid-style or written link based on width of screen 
function Login(props) {
    const updateWidth = () => {
        setWidth(window.innerWidth);
    };

    // Checks if user has resized window and updates width as necessary
    React.useEffect(() => {
        window.addEventListener("resize", updateWidth);
        return () => window.removeEventListener("resize", updateWidth);
    });

    const [width, setWidth] = React.useState(window.innerWidth);

    // Chooses whether to show written links or grid links based on window width
    return(<LoginHelper size={width} />)
}

export default Login;
