const DisplayMessage = ({info}) => {
    return ( 
        <div className="DisplayMessage">
            <h2>My messages:</h2>
            {info.map(MU => (
               <div className="previewMessage" key={MU.id}>
                   <p>{MU.message} - {MU.user}</p>
               </div>
           ))}
        </div>
     );
}
 
export default DisplayMessage;
